import React, { Component } from 'react';
import './ZoomControl.css';

class ZoomControl extends Component {

  zoomAction = () => {
    let map = document.getElementById("map")[0];
    console.log(map);
    //let map = window.google.maps.Map(document.getElementById('map')), {
    //document.getElementById("map"), {
      map.zoom = '11'
      //console.log('hi')

    //map.zoom = '11';

  }

  render() {
    return(

      <div id="control-div" className="control-div">
      <div id="control-ui" className="control-ui" title = "Click to set the map to Home"
      onClick= {this.zoomAction} >
        <div id="control-text" className="control-text">
          <p> Zoom </p>
        </div>
      </div>
      </div>

    )
  }
}

export default ZoomControl;
